import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaBars } from 'react-icons/fa'; // Import menu icon from react-icons
import './AdminDashboard.css';

const AdminDashboard = () => {
  // State to control dropdown visibility
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  // Toggle dropdown visibility when the menu icon is clicked
  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <div className="dashboard">
      {/* Navbar with menu icon on the left */}
      <nav className="navbar">
        <button className="menu-icon" onClick={toggleDropdown}>
          <FaBars size={24} /> {/* Menu icon */}
        </button>
        <h2 className="navbar-logo">Admin Dashboard</h2>
      </nav>

      {/* Dropdown Menu */}
      <div className="dropdown">
        {/* Conditionally render the dropdown content based on state */}
        {isDropdownOpen && (
          <div className="dropdown-content">
            <Link to="/upload">Add Student</Link>
            <Link to="/view-students">View Students</Link>
            <Link to="/add-menu-item">Add Menu Item</Link>
            <Link to="/view-menu">View Menu</Link>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
