// src/components/StudentLogin.js
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

const StudentLogin = () => {
  const [sid, setSid] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('dayscholar');
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8080/api/students/login', {
        sid,
        password,
        role
      });

      if (role === 'dayscholar') {
        history.push('/dayscholar');
      } else if (role === 'hostler') {
        history.push('/hostler');
      }
    } catch (err) {
      console.error(err);
      alert('Login failed!');
    }
  };

  return (
    <div>
      <h1>Student Login</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Student ID"
          value={sid}
          onChange={(e) => setSid(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <div>
          <label>
            <input
              type="radio"
              value="dayscholar"
              checked={role === 'dayscholar'}
              onChange={(e) => setRole(e.target.value)}
            />
            Day Scholar
          </label>
          <label>
            <input
              type="radio"
              value="hostler"
              checked={role === 'hostler'}
              onChange={(e) => setRole(e.target.value)}
            />
            Hostler
          </label>
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default StudentLogin;
