import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import MenuIcon from '@mui/icons-material/Menu'; // Import Material-UI Menu Icon
import './AdminDashboard.css';  // Import the CSS file for styling

const AdminDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false); // State to handle sidebar visibility

  // Function to toggle the sidebar
  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className="dashboard">
      {/* Navbar with a menu icon */}
      <nav className="navbar">
        <button className="menu-icon" onClick={toggleSidebar}>
          <MenuIcon fontSize="large" /> {/* Material-UI Menu Icon */}
        </button>
        <h2>Admin Dashboard</h2>
      </nav>

      {/* Sidebar - opens when menu icon is clicked */}
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <ul>
          <li><Link to="/upload">Add Student</Link></li>
          <li><Link to="/view-students">View Students</Link></li>
          <li><Link to="/add-menu-item">Add Menu Item</Link></li>
          <li><Link to="/view-menu">View Menu</Link></li>
        </ul>
      </div>

      {/* Main content goes here */}
      <div className="content">
        <h3>Welcome to the Admin Dashboard</h3>
        {/* Other content can be added here */}
      </div>
    </div>
  );
};

export default AdminDashboard;
